    const [sportData, setSportData] = useState({
        title: '',
        authorname: '',
        date: ''
    })
    const [editData, setEditData] = useState({})

    const handleChange = e => {
        setSportData({ ...sportData, [e.target.name]: e.target.value })
    }

    const handleSubmit = e => {
        e.preventDefault()
        axios.post('http://localhost:3002/api/sport/add', sportData)
        .then(res => window.location = '/sportsList')
        .catch(error => console.log(error))
    }

    //console.log(props.location.aboutProps)
    if(props.location.aboutProps){
    //const {authorname, title} = props.location.aboutProps
    setEditData(props.locatonaboutProps)
    }

    return (
        <div>
           {props.location.aboutProps ? <h1 
           style={{textAlign: 'center'}} >
               Edit sport</h1> :
                <h1 style={{textAlign: 'center'}}>
                    Add Sport</h1>}
            <form
            onSubmit={handleSubmit}
                style={{
                    display: 'flex',
                    flexDirection: 'column',
                    justifyContent: 'space-around'
                }}>
                <input value={title}  onChange={handleChange} type="text" name="title" placeholder="enter title" />
                <input value={authorname} onChange={handleChange} type="text" name="authorname" placeholder="enter authorName" />
                <input onChange={handleChange} type="date" name="date" placeholder="enter date" />
                <input type="submit" value="submit" />
            </form>
        </div>
    )
}

export default AddSport
