import React, { useState } from 'react';
import axios from 'axios';

const AddSport = () => {

    const [sportData, setSportData] = useState({
        title: '',
        authorName: '',
        date: ''
    })

    const handleChange = e => {
        setSportData({ ...sportData, [e.target.name]: e.target.value })
    }

    const handleSubmit = e => {
        e.preventDefault()
        axios.get('http://localhost:3002/api/sport/add', sportData)
        .then(res => window.location = '/spo')
        .catch(error => console.log(error))
    }

    return (
        <div>
            <h1>Add Sport</h1>
            <form
            onSubmit={handleSubmit}
                style={{
                    display: 'flex',
                    flexDirection: 'column',
                    justifyContent: 'space-around'
                }}>
                <input onChange={handleChange} type="text" name="title" placeholder="enter title" />
                <input onChange={handleChange} type="text" name="authorName" placeholder="enter authorName" />
                <input onChange={handleChange} type="date" name="date" placeholder="enter date" />
                <input type="submit" value="submit" />
            </form>
        </div>
    )
}

export default AddSport
