import React,{ useState, useEffect } from 'react'
import axios from 'axios';
import { Link } from 'react-router-dom';

function SportsList() {
    const [sport, setSport] = useState([]);
    useEffect(() => {
        axios.get('http://localhost:3002/api/sport/')
        .then(res => {setSport(res.data)})
        .catch(error => console.log(error))
    },[]);

    const deleteItem = (id) => {
        axios.delete(`http://localhost:3002/api/sport/${id}`)
        .then(res => {window.location = '/sportsList'})
    }


const renderList = () => sport && sport.map(item => (
    <div key={item._id} style={{padding: '20px', textAlign: 'center', marginBottom: '5px' , backgroundColor: "pink", border: '1px solid '}}>
         <h3>{item.title}</h3> 
         <h5>{item.authorname}</h5>
         <p>{item.date}</p>
         <button onClick={() => deleteItem(item._id)}>delete item</button>
         <Link to='/addSport' >Add Sport </Link>
         <Link to='/addSport' to >Edit Sport </Link>
    </div>
))
    return (
        <div>
            <h1>Sport News</h1>
            {renderList()}
        </div>
    )
}
export default SportsList
