        .then(res => {setSport(res.data)})
    }


const renderList = () => sport.map(item => (
    <div key={item._id} style={{padding: '20px', textAlign: 'center', margin: '5px' , backgroundColor: "pink", border: '1px solid '}}>
         <h3>{item.title}</h3> 
         <h5>{item.authorname}</h5>
         <p>{item.date}</p>
         <button onClick={deleteItem}>delete item</button>
    </div>
))
    return (
        <div>
            <h1>Sport News</h1>
            {renderList()}
        </div>
    )
}
export default SportsList
